<?phpclass validators_tel{    function check($value)    {        $value = mb_convert_kana($value, "n", 'UTF-8');        if (!preg_match("/(^(?<!090|080|070)(^\d{2,5}?\-?\d{1,4}?\-?\d{4}$|^[\d\-]{12}$))|(^(090|080|070)(\-?\d{4}\-?\d{4}|[\\d-]{13})$)|(^0120(\-?\d{2,3}\-?\d{3,4}|[\d\-]{12})$)|(^0080\-?\d{3}\-\d{4})/", $value)) {            return '電話番号の書式に誤りがあります';        //} elseif (strpos($value, '-') === false) {        //    return '\'-\'(ハイフン)区切りでお願いします';        } else {            return false;        }    }}